import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }
  acheter(){
 this.quantite--
  }
  titre:string='hello';
   blue:string='blue';
  person ={
    id:1,
    name:'test',
    age:20
  }
  quantite=4;
  imagesrc="/assets/img1.png";
  height=120;
  width=100;
  onInputChange(event:Event){
   const color=event.target as HTMLInputElement
   this.blue=color.value;
  }

}
<p>user works!</p>
<!-- <input (input)="onInputChange($event)">  -->
<div [style.color]="blue">{{titre}}</div>
{{person.id}} <br/>
<h2>{{quantite}}</h2>
<button [disabled]="quantite <=0" (click)="acheter()">acheter</button><br/>
<img [src]="imagesrc" [height]="height" [width]="width" />
<input name="c" [(ngModel)]="blue">
